// Client Id remains the same until browser reloads page
var clientIds = global.get('clientIds');
var cId = msg.network.meta._clientid;

// Set the token to be stored by the client
const clientStore = msg.network.meta.tiddlers.find((tiddler) => tiddler.title === '$:/temp/tw5-node-red/store');
if (!clientStore) {
    throw new Error('Tiddler $:/temp/tw5-node-red/store not present for bearer token')
}

// Initial connection so generate default values
// Send msg for further processing of new connections
if (cId && !clientIds[cId]) {
    const randomtext = cId;
    clientIds[cId] = {
        clientId: cId,
        userid: '', // Set when authenticated
        socketId: msg._socketId,
        auth: {
            _id: randomtext,
            _token: genToken(randomtext),
            authorized: false,
        }
    }

    // the server's socket id for this connection
    var connectDbTiddler = JSON.parse(connectionDb.getTiddlersAsJson(`[[${msg._socketId}]]`));
    if (connectDbTiddler.length === 0) {
        throw new Error('Connect ConnectionDb Tiddler not present for updates');
    }

    clientStore.token = clientIds[cId].auth._token;
    clientStore.socketId = msg._socketId;
    clientStore.clientid = cId;

    connectDbTiddler[0].tw5clientid = cId;
    connectDbTiddler[0].tags = cId;
    connectionDb.addTiddler(connectDbTiddler[0]);
    // Send to security system to finish processing first message
    node.send([null, msg]);
}

// Insure socket Id is correct (due to reconnects)
if (clientIds[cId].socketId !== msg._socketId) {
//   node.log(`Client switching from socket ${clientIds[cId].socketId}`);
//   node.log(` to socket ${msg._socketId}`);
    // Remove stale ConnectionDb tiddler with old socket id
    connectionDb.deleteTiddler(clientIds[cId].socketId);

    // Get the new ConnectionDb tiddler - created by tab 'Databases' -> 'ConnectionDb' flow 
    var connectDbTiddler = JSON.parse(connectionDb.getTiddlersAsJson(`[[${msg._socketId}]]`));
    if (connectDbTiddler.length === 0) {
        throw new Error('Reconnect ConnectionDb Tiddler not present for updates');
    }

    // Update all objects, server and client side
    clientIds[cId].socketId = msg._socketId
    clientStore.socketId = msg._socketId;
    connectDbTiddler[0].tw5clientid = cId;
    connectDbTiddler[0].tags = cId;
    connectionDb.addTiddler(connectDbTiddler[0]);
    // Send Heartbeat reply
    if (msg.topic === 'heartbeat') {
        node.send([msg, null]);
    } else { // Send to security system
        node.send([null, msg]);
    }
}

// Send every msg for further processing
