const $tw = global.get('$twmws'); // MWS TiddlyWiki
const mustache = global.get('mustache');
const search = msg.network.meta.location.search;

var app, mws;

var connstat = {
    title: '$:/plugins/multiwikiclient/SideBarSegment',
    tags: '$:/tags/SideBarSegment',
    'list-before': '$:/core/ui/SideBarSegments/page-controls',
    text: '{{$:/temp/tw5-node-red/netstat}}'
}

// Check for app name in URL search param ie: ?app=appname or ?mws=appname
app = /app=(\w+)/.exec(search) ? /app=(\w+)/.exec(search)[1] : '';
mws = /mws=(\w+)/.exec(search) ? /mws=(\w+)/.exec(search)[1] : '';

// No app requested in URL so run wiki without an app
if (!app && !mws) {
    msg.payload = [connstat];
    node.send([msg, null]);
    node.done();
    return;
}

if (app) {
    loadFromSync();
} else {
    loadFromMws();

}
msg.payload.push(connstat);

node.send([msg, null]);
node.done();

function loadFromSync() {
    var inputpath = `./public/app/${app}/tiddlers`;

    if (!$tw.utils.isDirectory(inputpath)) {
        msg.payload = [
            {
                title: 'Error: Application not found',
                text: `The application '${app}' is not found\n`
            }
        ];
        node.debug(`msg.inputpath: ${inputpath} is not found.`, msg);
        node.send([null, msg]);
        node.done();
        return;
    }

    const wiki = new $tw.Wiki;

    var tiddlers = $tw.loadTiddlersFromPath(inputpath);

    $tw.utils.each(tiddlers, (tiddlerInfo) => {
        $tw.utils.each(tiddlerInfo.tiddlers, (tiddler) => {
            wiki.importTiddler(new $tw.Tiddler(tiddler));
        });
    });

    msg.payload = JSON.parse(wiki.getTiddlersAsJson('[all[]]'));

    for (let i = 0; i < msg.payload.length; i++) {
        if (msg.payload[i].text && msg.payload[i].mustache === 'yes') {
            msg.payload[i].text = mustache.render(msg.payload[i].text, msg, {}, ['{%', '%}'])
        }
    }
    msg.app = app;
}

function loadFromMws() {
    var tiddlers = [];
    var recipeTiddlerList = $tw.mws.store.getRecipeTiddlers(mws);
    recipeTiddlerList.forEach(recipe => {
        tiddlers.push($tw.mws.store.getRecipeTiddler(recipe.title, mws).tiddler)
    })

    // mustache substitution
    for (let i = 0; i < tiddlers.length; i++) {
        if (tiddlers[i].text && tiddlers[i].mustache === 'yes') {
            tiddlers[i].text = mustache.render(tiddlers[i].text, msg, {}, ['{%', '%}'])
        }
    }
    msg.app = mws;
    node.send([null, null, msg]); // utimately start-watching-recipe  
    msg.payload = tiddlers;
}

return;

