//  (search for 'dice')
function getDiceIconTiddler(pips) {
    return `$:/images/bootstrap/dice-${pips}-fill`;
}

// Gets random whole number between 1 and 6
// https://stackoverflow.com/questions/1527803/generating-random-whole-numbers-in-javascript-in-a-specific-range
function getRandomDie() {
    let min = Math.ceil(1);
    let max = Math.floor(6);
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

// Initialize dice roll object
msg.dice = {
    username: msg.network.user.username,
    clientid: msg.network.meta._clientid,
    count: count,
    titles: [],
    sum: 0,
    list: ''
};

// Generate list of dice tiddler titles and sum them up
for (let i = 0; i < msg.dice.count; i++) {
    let pips = getRandomDie();
    msg.dice.titles.push(getDiceIconTiddler(pips));
    msg.dice.list += msg.dice.list ? ` ${pips}` : `${pips}`;
    msg.dice.sum += pips;
}

return msg;

// Dice session controls
var currentTiddler = msg.network.client.currentTiddler;

// Each client TiddlyWiki has server-side in-memory info by clientId
//  This is where session data will be stored
const clientIds = global.get('clientIds');

// Is this client a player?
function clientIsPlayer(cid) {
    return clientIds[cid].diceSession && clientIds[cid].diceSession.inDiceGame;
}

//  List of all player client ids
function getListOfPlayers() {
    var playerIds = [];
    for (let cid in clientIds) {
        if (clientIsPlayer(cid)) {
            playerIds.push(cid);
        }
    }
    return playerIds;
}

// Creates Dice Session Data
function initSessionData(username, cid, reset) {
    // Indicate client is watching dice rolls
    if (clientIds[cid] && (reset || typeof clientIds[cid].diceSession !== 'object')) {
        clientIds[cid].diceSession = {
            username: username,
            inDiceGame: true,
            leader: false,
            dice: {},
            won: 0, lost: 0, tie: 0, chg: 0,
            score: 0
        };
    }
}

// Increment player wins, losses, ties, and number die changed
function setStats() {
    if (msg.outcome.clientid.winner && clientIds[msg.outcome.clientid.winner]) {
        clientIds[msg.outcome.clientid.winner].diceSession.won++;
    }
    if (msg.outcome.clientid.loser && clientIds[msg.outcome.clientid.loser]) {
        clientIds[msg.outcome.clientid.loser].diceSession.lost++;
    }
    if (msg.outcome.clientid.tie1 && clientIds[msg.outcome.clientid.tie1]) {
        clientIds[msg.outcome.clientid.tie1].diceSession.tie++;
    }
    if (msg.outcome.clientid.tie2 && clientIds[msg.outcome.clientid.tie2]) {
        clientIds[msg.outcome.clientid.tie2].diceSession.tie++;
    }
    if (msg.outcome.clientid.chg && clientIds[msg.outcome.clientid.chg]) {
        clientIds[msg.outcome.clientid.chg].diceSession.chg++;
    }
}

function setPlayerTurn(cid, playerIds) {
    var nbrPlayers = playerIds.length;
    // When single player - is always 'your turn'
    msg.yourTurn = nbrPlayers > 1 ? false : true;
    // When clientId is not the current one it is their turn
    if (cid !== msg.network.meta._clientid) {
        msg.yourTurn = true;
    }
}

function setPlayerDiceRoll(cid) {
    clientIds[cid].diceSession.dice = JSON.parse(JSON.stringify(msg.dice));
}

// Tally scores
function calcScore() {
    var playerIds = getListOfPlayers();
    var highScore = -1, leader = '';
    // Calculate Scores
    playerIds.forEach((cid) => {
        let clientData = clientIds[cid].diceSession;
        let score =
            (clientData.won * 1)
            + (clientData.lost * -1)
            + (clientData.tie * 1)
            + (clientData.chg * 0);
        clientIds[cid].diceSession.score = score;

        // Track highest score (leader)
        clientIds[cid].diceSession.leader = false;
        if (clientIds[cid].diceSession.score > highScore) {
            highScore = clientIds[cid].diceSession.score;
            leader = cid;
        }
    })
    if (leader) {
        clientIds[leader].diceSession.leader = true;
    }

    var scores = [];
    playerIds.forEach((cid) => {
        scores.push(clientIds[cid].diceSession);
    })
    return scores;
}

// Loop thru the clientIds and send dice roll
//  to clients that have joined the game 
function sendToDicePlayers() {
    // Count the number of clients playing the game
    var playerIds = getListOfPlayers();
    msg.scores = calcScore();
    // Send tiddler to all playing the game
    playerIds.forEach((cid) => {
        // msg.broadcastId is for Broadcasting to client
        msg.broadcastId = cid;

        msg.playername = clientIds[cid].diceSession.username;
        // Remember this player's last roll
        if (cid === msg.network.meta._clientid) {
            setPlayerDiceRoll(cid);
            // 'Dice roll' tiddler not in TOC so display to story
            if (!currentTiddler.tags.includes('dice')) {
                msg.network.server.storylist.push('Dice roll');
            }
        }
        setPlayerTurn(cid, playerIds);
        msg.leader = clientIds[cid].diceSession.leader;
        // send to next node
        node.send([msg, null]);
    })
}

function reset() {
    var playerIds = getListOfPlayers();
    msg.resetApproved = false;
    playerIds.forEach((cid) => {
        if (clientIds[cid].diceSession.leader && cid === msg.network.meta._clientid) {
            msg.resetApproved = true;
        }
    })
    if (msg.resetApproved) {
        playerIds.forEach((cid, idx) => {
            initSessionData(clientIds[cid].diceSession.username, cid, true);
        })
        global.set('prevDice', null);
        msg.outcome = {
            prevInfo: 'Reset!',
            rollInfo: '',
            clientid: {
                winner: '',
                loser: '',
                tie1: '',
                tie2: '',
                chg: '',
            }
        }
    }
}


// Insure have dice session data for this client
initSessionData(msg.network.user.username, msg.network.meta._clientid);

if (currentTiddler.reset === 'yes') {
    reset();
}

// Increment player wins and losses
setStats();
// Send dice roll to each player
sendToDicePlayers();
node.done();

// Previous roll is stored in node-red global context
msg.prevDice = global.get('prevDice');

// Text to display and client ids of outcomes
msg.outcome = {
    prevInfo: '&nbsp;',
    rollInfo: '',
    clientid: { winner: '', loser: '', tie1: '', tie2: '', chg: '' }
}

// Outcome logic
//  Sets msg.outcome.prevInfo to text to display about previous roll
//  returns text of the result when comparing the previous
//    roll (msg.prevDice) to the players current roll (msg.dice)
//
// Note - the order of 'if' statements is important
function rollOutcome() {
    // No previous roll - so this is the first roll
    if (!msg.prevDice) {
        msg.outcome.prevInfo = 'First roll';
        return '';
    }
    // Number of dice rolled has changed
    if (msg.dice.count !== msg.prevDice.count) {
        msg.outcome.prevInfo = 'Number of dice has changed';
        msg.outcome.clientid.chg = msg.dice.clientid;
        return '';
    }
    // Same user has rolled again
    if (msg.dice.clientid === msg.prevDice.clientid) {
        msg.outcome.prevInfo = `${msg.prevDice.username} re-roll!`;
        return ``;
    }

    // Pre-load previous info text with previous roll to be beat
    msg.outcome.prevInfo = ` against ${msg.prevDice.username}'s (${msg.prevDice.count} die) roll of ${msg.prevDice.sum}`

    // Check for a tie
    if (msg.dice.sum === msg.prevDice.sum) {
        msg.outcome.clientid.tie1 = msg.dice.clientid;
        msg.outcome.clientid.tie2 = msg.prevDice.clientid;
        return 'A tie!';
    }

    // Check if players roll beats the previous roll
    if (msg.dice.sum > msg.prevDice.sum) {
        msg.outcome.clientid.winner = msg.dice.clientid;
        msg.outcome.clientid.loser = msg.prevDice.clientid;
        return `${msg.dice.username} wins!`;
    }

    // Must be that players roll does not beat the previous roll
    msg.outcome.clientid.winner = msg.prevDice.clientid;
    msg.outcome.clientid.loser = msg.dice.clientid;
    return `${msg.dice.username} lost!`;
}

// Compare Players roll with previous roll
msg.outcome.rollInfo = rollOutcome();

// Players dice roll to globaal context
//  (when moving data from 'msg' to global context - insure is a copy)
global.set('prevDice', JSON.parse(JSON.stringify(msg.dice)));

return msg;

